#!/usr/bin/env node

'use strict';

var app = require('cmdu');

app.version = require('../package.json').version;

app.action(function () {
    this.showHelp();
});

app.command('create [name]')
    .describe('新建一个项目')
    .describe('name', '项目名称, 使用英文字母或者数字')
    .use('./create');

app.command('server')
    .describe('在当前目录启动一个静态文件服务')
    .option('-p, --port', '端口号', '8088')
    .use('./server');

app.listen();